import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  count: 0,
};

export const getId = createSlice({
  name: "count",
  initialState,
  reducers: {
    increment(state) {
      state.count += 2;
    },
    decrement(state) {
      state.count -= 2;
    },
    countNull(state) {
      state.count == 0;
    },
  },
});

export const { increment, decrement, countNull } = getId.actions;
export default getId.reducer;
