import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  todos: [],
};

export const todoList = createSlice({
  name: "todo",
  initialState,
  reducers: {
    addTask(state, action) {
      state.todos = action.payload;
    },
  },
});

export const { addTask } = todoList.actions;
export default todoList.reducer;
