import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  todos: [],
};

export const todoList = createSlice({
  name: "todo",
  initialState,
  reducers: {
    addTask(state, action) {
      state.todos.push(action.payload)
    },
    toogleComplead(state, action) {
        const toogleHen = state.todos.find(todo => todo.id === action.payload);
        toogleHen.complead = !toogleHen.complead;
    }
  },
});

export const { addTask, toogleComplead } = todoList.actions;
export default todoList.reducer;
