import { createSlice } from "@reduxjs/toolkit";

//first value
const initialState = {
  count: 15,
};

//name const for use ceateSelector
const changeCount = createSlice({
  //name
  name: "changeCount",
  //first value
  initialState,
  //object with our function
  reducers: {
    //function increment
    increment(state) {
      state.count += 1;
    },
    decrement(state) {
      state.count -= 1;
    },
  },
});

//giv our function
export const { increment, decrement } = changeCount.actions;

//it's must be :)
export default changeCount.reducer;
