import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setFirstName, setLastName } from './redux/user/userSlice';

export default function ChildComponent() {
    const dispatch = useDispatch();
    const name = useSelector(state => state.user.firstName)
    const lastName = useSelector(state => state.user.lastName)

    return (
        <div>
            <input type="text" onChange={event => dispatch(setFirstName(event.target.value))} placeholder='first name' />
            <input type="text" onChange={event => dispatch(setLastName(event.target.value))} placeholder='last name' />
            <p>First name: {name}</p>
            <p>Last name: {lastName}</p>
        </div>
    )
}
