import React from 'react'

export default function Categories() {
  const [pizzaIndex, setPizzaIndex] = React.useState(0)
  return (
    <div className="categories">
      <ul>
        <li className={pizzaIndex === 0 ? "active" : ''}>Все</li>
        <li className={pizzaIndex === 1 ? "active" : ''}>Мясные</li>
        <li className={pizzaIndex === 2 ? "active" : ''}>Вегетарианская</li>
        <li className={pizzaIndex === 3 ? "active" : ''}>Гриль</li>
        <li className={pizzaIndex === 4 ? "active" : ''}>Острые</li>
        <li className={pizzaIndex === 5 ? "active" : ''}>Закрытые</li>
      </ul>
    </div>
  );
}
