import React from 'react'

export default function Categories() {
  const [pizzaIndex, setPizzaIndex] = React.useState(0);
  const onClickCategory = (index) => {
    setPizzaIndex(index)
  }

  const arrayPizza = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые']

  return (
    <div className="categories">
      <ul>
        {arrayPizza.map((value, i) => <li onClick={() => onClickCategory(5)} className={pizzaIndex === 5 ? "active" : ''}>{value}</li>)}
      </ul>
    </div>
  );
}
