import React from 'react';
import TodoTask from './TodoTask';

import { addTask } from '../redux/todo/todoSlice';
import { useDispatch, useSelector } from 'react-redux';
import { v4 } from 'uuid';

export default function ToDoList() {
  const [testValue, setTextValue] = React.useState('');
  const dispatch = useDispatch();

  const todoArr = useSelector(state => state.todo.todos);

  const addToDoTask = () => {
    const todo = {
      id: v4(),
      text: testValue,
      complated : false
    }

    dispatch(addTask(todo));
    setTextValue('')
  }

  return (
    <div>
      <input value={testValue} onChange={(e) => setTextValue(e.target.value)} type="text" placeholder='write your tasks' />
      <button onClick={() => addToDoTask()}>submit</button>

      <div>
        <TodoTask />
      </div>
    </div>
  )
}
