import { createSlice } from "@reduxjs/toolkit";

//first value
const initialState = {
  count: o,
};

//name const for use ceateSelector
const changeCount = createSlice({
  //name
  name: "changeCount",
  //first value
  initialState,
  //object with our function
  reducers: {
    //function increment
    increment(state) {
      state.count++;
    },
  },
});

//giv our function
export const { increment } = changeCount.actions;

//it's must be :)
export default changeCount.reducer;
