import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  count: 0,
};

const getCount = createSlice({
  name : 'count',
  initialState,
  reducer : {
    increment(state) {
      state.count += 1
    },
    decrement(state) {
      state.count -= 1
    }
  }
})

export const {increment, decrement} = getCount.actions
export default getCount.reducer
