import React from 'react'

export default function Categories({value}) {
  console.log(value)

  const [pizzaIndex, setPizzaIndex] = React.useState(0);
  const onClickCategory = (index) => {
    setPizzaIndex(index)
  }

  const arrayPizza = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Закрытые']

  return (
    <div className="categories">
      <ul>
        {arrayPizza.map((value, i) => <li key={i} onClick={() => onClickCategory(i)} className={pizzaIndex === i ? "active" : ''}>{value}</li>)}
      </ul>
    </div>
  );
}
