import React from 'react'

export default function Categories() {
  const [pizzaIndex, setPizzaIndex] = React.useState(0);
  const onClickCategory = (index) => {
    setPizzaIndex(index)
  }

  return (
    <div className="categories">
      <ul>
        <li onClick={() => onClickCategory(0)} className={pizzaIndex === 0 ? "active" : ''}>Все</li>
        <li onClick={() => onClickCategory(1)} className={pizzaIndex === 1 ? "active" : ''}>Мясные</li>
        <li onClick={() => onClickCategory(2)} className={pizzaIndex === 2 ? "active" : ''}>Вегетарианская</li>
        <li onClick={() => onClickCategory(3)} className={pizzaIndex === 3 ? "active" : ''}>Гриль</li>
        <li onClick={() => onClickCategory(4)} className={pizzaIndex === 4 ? "active" : ''}>Острые</li>
        <li onClick={() => onClickCategory(5)} className={pizzaIndex === 5 ? "active" : ''}>Закрытые</li>
      </ul>
    </div>
  );
}
