import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  firstName: "",
  lastName: "",
};

export const userSlice = createSlice({
  name: "user",
  initialState,
  reducers: {
    setFirtsName: (state, action) => {
      state.firstName = action.payload;
    },
  },
});

export const { setFirtsName } = userSlice.actions;

